mapper.select:
SELECT r.id, r.resource_code, r.resource_name, r.resource_url, r.sort, r.parent_id, r.is_leaf, r.type, r.state FROM sy_role_resource rr LEFT JOIN sy_resource r ON rr.resource_id = r.id WHERE rr.delflag = '1' and r.delflag = '1' and rr.role_id in (
#{roleId}
)

select * from sy_resource where delflag = '1'

    and resource_code like #{model.resourceCode}


    and resource_name like #{model.resourceName}


    and type = #{model.type}


    and create_time >= #{model.startTime}


    and #{model.endTime} >= create_time

limit ${model.page},${model.rows}

select count(1) from sy_resource where delflag = '1'

    and resource_code like #{model.resourceCode}


    and resource_name like #{model.resourceName}


    and type = #{model.type}


    and create_time >= #{model.startTime}


    and #{model.endTime} >= create_time
